import React, { Component } from 'react';
import './App.css';
import Cards from "./cards.json";
import Wrapper from "./components/Wrapper";
import Card from "./components/Card";
import Score from "./components/ScoreCard"


class App extends Component {

  state = {
    Cards,
    // clickedCardIds: [],
    // goal: 12,
    // score: 0,
    // status: "",

    clickedArray: [],
    topScore: 0,
    score: 0,
    message: "",
    shakeit: "false"
  };


  // shuffleScoreCard = id => {
  //   let clickedCardIds = this.state.clickedCardIds;

  //   if(clickedCardIds.includes(id)){
  //     this.setState({ clickedCardIds: [], score: 0, status:  "Game Over! You lost. Click to play again!" });
  //     return;
  //   }else{
  //     clickedCardIds.push(id)

  //     if(clickedCardIds.length === 12){
  //       this.setState({score: 12, status: "You Win!", clickedCardIds: []});
  //       console.log('You Win');
  //       return;
  //     }

  //     this.setState({ Cards, clickedCardIds, score: clickedCardIds.length, status: " " });

  //     for (let i = Cards.length - 1; i > 0; i--) {
  //       let w = Math.floor(Math.random() * (i + 1));
  //       [Cards[i], Cards[w]] = [Cards[w], Cards[i]];
  //     }
  //   }
  // }

  clickPicture = id => {
    const shuffledArray = this.shuffleArray(Cards);
    this.setState({Cards: shuffledArray});
    if (this.state.clickedArray.includes(id)) {
      this.setState({ score: 0, clickedArray: [], message: "Incorrect, Game Over. Click an image to start again!", shakeit: "true"});
    }
    else {
      this.setState({
        clickedArray: this.state.clickedArray.concat([id]),
        score: this.state.score + 1,
        message: "Correct",
        shakeit: "false"
      });
    }
    // set topscore = score if score>topscore.
    if (this.state.score > this.state.topScore) {
      this.setState({ topScore: this.state.score });
    }
    // shake the wrapper if shakeit is set to true
  }
  shuffleArray = (array) => {
      for (let i = array.length - 1; i > 0; i--) {
          const w = Math.floor(Math.random() * (i + 1));
          [array[i], array[w]] = [array[w], array[i]];
      }
      return array;
  }

  render() {
    return (
      <div className="App">
       <nav className="navbar navbar-dark bg-dark">
          <span className="navbar-brand mb-0 h1">Clicky-Game</span>
       </nav>
       <h3 className="App-intro"> 
          <p className = "score"><strong>Score: {this.state.score} | Best: {this.state.topScore}</strong></p>
          <p className="message"><strong>{this.state.message}</strong></p>
        </h3>
               <Wrapper
        shakeWrapper = {this.state.shakeit}
        pictures=
          {this.state.Cards.map(Cards => (
            <Card
              clickPicture={this.clickPicture}
              id={Cards.id}
              key={Cards.id} // to get rid of unique key prop warning
              name={Cards.name}
              image={Cards.image}
            />
          ))}
        />
       {/* <Wrapper>
          {this.state.Cards.map(Cards => (
            <Card
              shuffleScoreCard={this.shuffleScoreCard}
              id={Cards.id}
              key={Cards.id}
              image={Cards.image}
            />
          ))}
        </Wrapper> */}
        <footer>Copyright &copy;</footer>
      </div>
    );
  }
}

export default App;
