import './App.css';
import Cards from "./cards.json";
import Wrapper from "./components/Wrapper";
import Card from "./components/Card";


class App extends Component {

  state = {
    Cards
  };


  shuffleScoreCard = id => {
    let clickedCard = this.state.clickedPuppyIds;

    if(clickedPuppyIds.includes(id)){
      this.setState({ clickedPuppyIds: [], score: 0, status:  "Game Over! You lost. Click to play again!" });
      return;
    }else{
      clickedPuppyIds.push(id)

      if(clickedPuppyIds.length === 8){
        this.setState({score: 8, status: "You Won! Great Job, Smartie! Click to play again!", clickedPuppyIds: []});
        console.log('You Win');
        return;
      }

      this.setState({ Cards, clickedPuppyIds, score: clickedPuppyIds.length, status: " " });

      for (let i = Cards.length - 1; i > 0; i--) {
        let j = Math.floor(Math.random() * (i + 1));
        [Cards[i], Cards[j]] = [Cards[j], Cards[i]];
      }
    }
  }

  render() {
    return (
      <div className="App">
       <nav class="navbar navbar-dark bg-dark">
          <span class="navbar-brand mb-0 h1">Score</span>
       </nav>
       <Wrapper>
          {this.state.Cards.map(Cards => (
            <Card
              id={Cards.id}
              key={Cards.id}
              image={Cards.image}
            />
          ))}
        </Wrapper>
      </div>
    );
  }
}

export default App;
