import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import cards from "./cards.json";

class App extends Component {

  state = {
    cards,
    clickedCardsId: [],
    score: 0,
    goal: 12,
    status: ""
  };

  shuffleScoreCard = id => {
    let clickedCardsId = this.state.clickedCardsId;

    if(clickedCardsId.includes(id)){
      this.setState({ clickedCardsId: [], score: 0, status:  "Game Over! You lost. Click to play again!" });
      return;
    }else{
      clickedCardsId.push(id)

      if(clickedCardsId.length === 8){
        this.setState({score: 8, status: "You Won! Great Job, Smartie! Click to play again!", clickedCardsId: []});
        console.log('You Win');
        return;
      }

      this.setState({ cards, clickedCardsId, score: clickedCardsId.length, status: " " });

      for (let i = cards.length - 1; i > 0; i--) {
        let j = Math.floor(Math.random() * (i + 1));
        [cards[i], cards[j]] = [cards[j], cards[i]];
      }
    }
  }


  render() {
    return (
      <div className="App">
       <nav class="navbar navbar-dark bg-dark">
          <span class="navbar-brand mb-0 h1">Navbar</span>
       </nav>
       <div className="App">
        <header className="App-header">
          <h1 className="App-title">The Clickster</h1>
          <p className="App-intro">
            Try not to click the same image twice!
          </p>
        </header>
        <Score total={this.state.score}
               goal={8}
               status={this.state.status}
               />
        <Wrapper>
          {this.state.pups.map(puppy => (
            <Card
              shuffleScoreCard={this.shuffleScoreCard}
              id={puppy.id}
              key={puppy.id}
              image={puppy.image}
            />
          ))}
        </Wrapper>
        <footer>
          <p>Designed and built by Heather Mathies. You can find the
          code<a href="https://github.com/hmathies/Clicky-Game" target="_blank" rel="noopener noreferrer"> here</a>.</p>
        </footer>
    </div>
      </div>
    );
  }
}

export default App;
